import {Button,Header,Icon,Form,Input,TextArea,Image,Message} from 'semantic-ui-react';
import React from 'react';
import axios from 'axios';
import baseUrl from '../utils/baseUrl';
import CatchErros from '../utils/catchErrors';
function CreateProduct() {
  const INITIAL_RPODUCT={
    name:'',
    price:'',
    media:'',
    description:''
  }
  const [product,setProduct]=React.useState({
    name:'',
    price:'',
    media:'',
    description:''
  })
  const[mediaPreview,setMediaPreview]=React.useState('');
  const[success,setSuccess]=React.useState(false);
  const[loading,setLoading]=React.useState(false);
  const[disabled,setDisabled]=React.useState(true);
  const[error,setError]=React.useState('');
  React.useEffect(()=>{
    const isProduct=Object.values(product).every(el=>Boolean(el));
    isProduct?setDisabled(false):setDisabled(true);
  }

  )
  function handleChange(event){
    const {name,value,files}=event.target;
    if(name==='media'){
      setProduct(prevState=>({...prevState,media:files[0]}))
      setMediaPreview(window.URL.createObjectURL(files[0]));
    }
    else{
      setProduct((prevState)=>({...prevState,[name]:value}))
    }

  }
  async function handleImageUpload(){
    const data=new FormData();
    data.append('file',product.media);
    data.append('upload_preset','reactreserve');
    data.append('cloud_name','bishwas');
    const response=await axios.post(process.env.CLOUDINARY_URL,data);
    const mediaUrl=response.data.url;
    return mediaUrl;

  }
  async function handleSubmit(event){
    try{
      event.preventDefault();
      setLoading(true);
      const mediaUrl=await handleImageUpload();
      console.log({mediaUrl});
      const url=`${baseUrl}/api/product`;
      const {name,price,description}=product;
      const payload={name,price,description,mediaUrl};
      await axios.post(url,payload);
      setProduct(INITIAL_RPODUCT);
      setSuccess(true);
    }catch(error){
       CatchErros(error,setError);
    }
    finally{
      setLoading(false);
    }

  }
  return <>
      <Header as="h2" block>
        <Icon name="add" color="orange" />
        Create New Product
      </Header>
      <Form onSubmit={handleSubmit} loading={loading}  error={Boolean(error)} success={success}>
      <Message
          success
          icon="check"
          header="Success!"
          content="Your product has been posted"
        />
        <Message
          error
          header="Oops !"
          content={error}
        />
        <Form.Group widths="equal">
          <Form.Field
            control={Input}
            name="name"
            label="Name"
            placeholder="Name"
           value={product.name}
           onChange={handleChange}
          />
          <Form.Field
            control={Input}
            name="price"
            label="Price"
            placeholder="Price"
            min="0.00"
            step="0.01"
            type="number"
           value={product.price}
            onChange={handleChange}
          />
          <Form.Field
            control={Input}
            name="media"
            type="file"
            label="Media"
            accept="image/*"
            content="Select Image"
            onChange={handleChange}
          />

        </Form.Group>
        <Image src={mediaPreview} rounded centered size="small"/>

        <Form.Field
          control={TextArea}
          name="description"
          label="Description"
          placeholder="Description"
          onChange={handleChange}
          value={product.description}
        />
        <Form.Field
          control={Button}
          color="blue"
          icon="pencil alternate"
          disabled={disabled||loading}
          content="Submit"
          type="submit"
        />
      </Form>
    </>
}

export default CreateProduct;
